import React, { useEffect} from "react";
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { fetchOrderByUserRequest } from '../redux/slices/orderSlice';
import { createDispatchRequest } from '../redux/slices/dispatchSlice';

const OrderForUser = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const { order, loading, error } = useSelector((state) => state.order);

    useEffect(() => {
        dispatch(fetchOrderByUserRequest());
    }, [dispatch]);

    const handlePickup = (orderId) => {
        const data = { orderId, navigate };
        dispatch(createDispatchRequest(data));

    };

    const renderButton = (status) => {
        switch (status) {
            case 'order created':
                return 'Pending pick up';
            case 'waiting for pickup':
                return 'Waiting for pick up';
            case 'Item dispatched':
                return 'Confirm pick up';
            case 'Item delivered':
                return 'Confirm delivery';
            case 'Order completed':
                return 'Order completed';
            default:
                return <p>No valid status found</p>;
        }
    };

    if (loading) return <p>Loading order...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div className="min-h-screen p-6 bg-gray-100">
        <h2 className="text-lg font-semibold mb-4">Order Details</h2>
        <div className="bg-white border border-gray-300 rounded-lg shadow-lg p-6">
          {order ? (
            <table className="min-w-full bg-white">
              <tbody>
                <tr className="border-b">
                  <th className="py-3 px-4 text-left">Email:</th>
                  <td className="py-3 px-4">{order.user_email}</td>
                </tr>
                <tr className="border-b">
                  <th className="py-3 px-4 text-left">Pick-Up Address:</th>
                  <td className="py-3 px-4">{order.pick_up_address}</td>
                </tr>
                <tr className="border-b">
                  <th className="py-3 px-4 text-left">Delivery Address:</th>
                  <td className="py-3 px-4">{order.delivery_address}</td>
                </tr>
                <tr className="border-b">
                  <th className="py-3 px-4 text-left">Description:</th>
                  <td className="py-3 px-4">{order.description}</td>
                </tr>
                <tr>
                  <th className="py-3 px-4 text-left">Status:</th>
                  <td className="py-3 px-4">{order.status}</td>
                </tr>
              </tbody>
            </table>
          ) : (
            <p className="text-center py-4">No order details available</p>
          )}
        </div>
      </div>
    //     <div>
    //     <h2>Orders</h2>
    //     {!order || !Array.isArray(order) || order.length === 0 ? (
    //         <p>{order}</p>
    //     ) : (
    //         <ul>
    //             {order.map((orders) => (
    //                 <li key={orders._id}>
    //                     {orders.pickup_address}
    //                     <button onClick={() => handlePickup(orders._id)}>
    //                         {renderButton(orders.status)}
    //                     </button>
    //                 </li>
    //             ))}
    //         </ul>
    //     )}
    // </div>
    );
};

export default OrderForUser;
