import {call, put, takeLatest} from 'redux-saga/effects'
import axios from 'axios'
import {fetchUsersRequest, createUserfetchUsersSuccess, fetchUsersFailure} from '../slices/userSlice'

function* fetchUsersSaga(){
    try {
        const response = yield call(axios.get, 'http://localhost:4000/api/users')
        yield put(fetchUsersSuccess(response.data))
    } catch (error) {
        yield put(fetchUsersFailure(error.response.data.message))
    }
}
function* createUserSaga(action){
    const {credentials,navigate} = action.payload
    try {
        const response = yield call(axios.post,'http://localhost:4000/api/login', credentials);
        console.log(response)
        yield put(createUser(response.data))
        navigate('/Users')
    } catch (error) {
        yield put(loginFailure(error.message))
    }
}
function* userSaga(){
    yield takeLatest(fetchUsersRequest.type, fetchUsersSaga)
    yield takeLatest(fetchUsersRequest.type, createUserSaga)
}

export default userSaga
