import {createSlice} from '@reduxjs/toolkit';

const initialState = {
    dispatches: [],
    loading: false,
    error:null,
};

const dispatchSlice = createSlice({
    name:'dispatch',
    initialState,
    reducers:{
        fetchDispatchRequest:(state)=>{
            state.loading = true
        },
        fetchDispatchSuccess:(state,action)=>{
            state.dispatch= action.payload;
            state.loading=false
        },
        fetchDispatchFailure:(state,action)=>{
            state.error = action.payload
        },
        createDispatchRequest:(state)=>{
            state.loading=true
        },
        createDispatchSuccess:(state,action)=>{
            state.dispatch.push(action.payload)
            state.loading=false
        },
        createDispatchFailure:(state,action)=>{
            state.error=action.payload
            state.loading=false
        }
    }
})

export const {fetchDispatchRequest,fetchDispatchSuccess,fetchDispatchFailure,createDispatchRequest,createDispatchSuccess,createDispatchFailure} = dispatchSlice.actions

export default dispatchSlice.reducer
