import React, {useState} from "react";
import {useDispatch} from 'react-redux'
import {useNavigate} from 'react-router-dom'
import { createOrderRequest } from '../redux/slices/orderSlice'


const CreateOrders = () =>{
    const dispatch = useDispatch()
    const navigate = useNavigate()

    const [details,setDetails] = useState({user_id:'',user_email:'',description:'',pickup_address:'',delivery_address:''})
    const userId = localStorage.getItem('userId');
    const userEmail = localStorage.getItem('userEmail');

    const handleChange = (e)=>{
        setDetails({...details, [e.target.name]:e.target.value})
    }

    const handleSubmit = (e) =>{
        e.preventDefault()
        details.user_id=userId
        details.user_email=userEmail
        const data ={details,navigate}
        dispatch(createOrderRequest(data))
    }

    return(
        <div>
            <h2>Create driver</h2>
            <form onSubmit={handleSubmit}>
            <input
                name="user_id"
                type="hidden"
                value={userId}
                onChange={handleChange}
                />
            <input
                name="user_email"
                type="hidden"
                value={userEmail}
                onChange={handleChange}
                />
             <textarea
                name="description"
                placeholder="Describe item"
                value={details.description}
                onChange={handleChange}
                required
                rows="4" // Adjust this to control the number of visible rows
                cols="50" // Adjust this to control the width of the textarea
            />

                      <input
                name="pickup_address"
                placeholder="pickup_address"
                type="text"
                value={details.pickup_address}
                onChange={handleChange}
                />
                      <input
                name="delivery_address"
                placeholder="delivery_address"
                type="text"
                value={details.delivery_address}
                onChange={handleChange}
                />
                <button type="submit">Submit</button>
            </form>
        </div>
    )
}
export default CreateOrders
