import React,{useEffect} from "react";
import {useDispatch,useSelector} from 'react-redux'
import {fetchUsersRequest} from '../redux/slices/userSlice'

const Users = () => {
    const dispatch = useDispatch()
    const {users, loading,error} = useSelector((state)=>state.users)

    useEffect(()=>{
        dispatch(fetchUsersRequest())
    },[dispatch])

    if(loading)return <p>Loading users...</p>
    if(error)return <p>Error:{error}</p>

    return(
        <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-4">
        <h2 className="text-3xl font-bold mb-8 text-center">Users List</h2>

        {/* Users List */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 w-full max-w-6xl">
          {users.map((user, index) => (
            <div key={index} className="bg-white p-6 rounded-lg shadow-lg">
              {/* Full Name */}
              <div>
                <p className="text-sm text-gray-500">Full Name</p>
                <p className="text-lg font-semibold">{user.fullName}</p>
              </div>

              {/* Email */}
              <div className="mt-2">
                <p className="text-sm text-gray-500">Email</p>
                <p className="text-lg font-semibold">{user.email}</p>
              </div>

              {/* Role */}
              <div className="mt-2">
                <p className="text-sm text-gray-500">Role</p>
                <p className="text-lg font-semibold">{user.role}</p>
              </div>

              {/* Phone */}
              <div className="mt-2">
                <p className="text-sm text-gray-500">Phone Number</p>
                <p className="text-lg font-semibold">{user.phone}</p>
              </div>

              {/* Additional User Information */}
              <div className="mt-2">
                <p className="text-sm text-gray-500">Address</p>
                <p className="text-lg font-semibold">{user.address}</p>
              </div>

              {/* Additional User Information */}
              <div className="mt-2">
                <p className="text-sm text-gray-500">Date of Birth</p>
                <p className="text-lg font-semibold">{user.dateOfBirth}</p>
              </div>
            </div>
          ))}
        </div>
      </div>
        // <div>
        //     <h2>Users</h2>
        //     <ul>
        //         {users.map((user)=>(
        //             <li key={user._id}>{user.name}</li>
        //         ))}
        //     </ul>
        // </div>
    )
}

export default Users
