import React, {useState,useEffect} from "react";
import {useDispatch,useSelector} from 'react-redux'
import {useNavigate} from 'react-router-dom'
import { createDriverRequest } from '../redux/slices/driverSlice'
import {fetchUsersRequest} from '../redux/slices/userSlice'
import {fetchVehicleRequest} from '../redux/slices/vehicleSlice'



const CreateDrivers = () =>{
    const dispatch = useDispatch()
    const navigate = useNavigate()

    const [details,setDetails] = useState({type:'',capacity:'',license_plate:''})
    const {users} = useSelector((state)=>state.users)
    const {vehicles} = useSelector((state)=>state.vehicle)


    const handleChange = (e)=>{
        setDetails({...details, [e.target.name]:e.target.value})
    }

    const handleSubmit = (e) =>{
        e.preventDefault()
        const data ={details,navigate}
        dispatch(createDriverRequest(data))
    }
    useEffect(()=>{
        dispatch(fetchUsersRequest())
    },[dispatch])
    useEffect(()=>{
        dispatch(fetchVehicleRequest())
    },[dispatch])
    console.log('>>>>>>',users,vehicles)
    return(
        <div>
            <h2>Create driver</h2>
            <form onSubmit={handleSubmit}>
            <label>Select a User:</label>
            <select value={selectedUser} onChange={handleChange}>
                <option value="">-- Select a User --</option>
                {data.map((item) => (
                    <option key={item._id} value={item.user_id.name}>
                        {item.user_id.name}
                    </option>
                ))}
            </select>
                <input
                name="capacity"
                placeholder="Capacity"
                type="text"
                value={details.capacity}
                onChange={handleChange}
                />
                <input
                name="license_plate"
                placeholder="License plate"
                type="text"
                value={details.license_plate}
                onChange={handleChange}
                />              
                <button type="submit">Submit</button>
            </form>
        </div>
    )
}
export default CreateDrivers
