import React,{useEffect} from "react";
import {useDispatch,useSelector} from 'react-redux'
import {useNavigate} from 'react-router-dom'
import {fetchOrderRequest} from '../redux/slices/orderSlice'
import {createDispatchRequest} from '../redux/slices/dispatchSlice'

const Order = () => {
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const {order,loading,error} = useSelector((state)=>state.order)

    useEffect(()=>{
        dispatch(fetchOrderRequest())
    },[dispatch])

    const handlePickup = (orderId) =>{
        console.log(">>>>>",orderId)
        const data = {orderId,navigate}
        dispatch(createDispatchRequest(data))
    }

    if(loading)return <p>Loading order...</p>
    if(error)return <p>Error:{error}</p>

    return(
        <div>
            <h2>Orders</h2>
            <ul>
                {order.map((orders)=>(
                    <li key={orders._id}>
                        {orders.pickup_address} 
                        <button onClick={()=>handlePickup(orders._id)}>
                            {console.log(order.status)}
                            {order.status==='order created'?'Pick up':'Waiting for pick up'}
                            </button>
                        </li>
                ))}
            </ul>
        </div>
    )
}

export default Order
