import React, { useEffect} from "react";
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { fetchDriverOrderRequest } from '../redux/slices/orderSlice';
import { createDispatchRequest } from '../redux/slices/dispatchSlice';

const Order = () => {
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const { order, loading, error } = useSelector((state) => state.order);

    useEffect(() => {
        dispatch(fetchDriverOrderRequest());
    }, [dispatch]);

    const handlePickup = (orderId) => {
        const data = { orderId, navigate };
        dispatch(createDispatchRequest(data));

    };

    const renderButton = (status) => {
        switch (status) {
            case 'order created':
                return 'Pick up';
            case 'waiting for pickup':
                return 'Waiting for pick up';
            case 'Item dispatched':
                return 'Item dispatched';
            case 'Item delivered':
                return 'Item delivered';
            default:
                return <p>No valid status found</p>;
        }
    };

    if (loading) return <p>Loading order...</p>;
    if (error) return <p>Error: {error}</p>;

    return (
        <div>
            <h2>Orders</h2>
            <ul>
                {
                    <li key={order._id}>
                        {order.pickup_address}
                        <button onClick={() => handlePickup(order._id)}>
                        {renderButton(order.status)}
                        </button>
                    </li>
                }
            </ul>
        </div>
    );
};

export default Order;
