import React, {useState,useEffect} from "react";
import {useDispatch,useSelector} from 'react-redux'
import {useNavigate} from 'react-router-dom'
import { createDriverRequest } from '../redux/slices/driverSlice'
import {fetchUsersRequest} from '../redux/slices/userSlice'
import {fetchVehicleRequest} from '../redux/slices/vehicleSlice'



const CreateDrivers = () =>{
    const dispatch = useDispatch()
    const navigate = useNavigate()

    const [selectedUser,setSelectedUser] = useState("")
    const [selectedVehicle,setSelectedVehicle] = useState("")
    const {users} = useSelector((state)=>state.users)
    const {token} = use
    const {error} = useSelector((state)=>state.driver)
    const {vehicles} = useSelector((state)=>state.vehicle)


    const handleUserChange = (e)=>{
        setSelectedUser(e.target.value)
    }
    const handleVehicleChange = (e)=>{
        setSelectedVehicle(e.target.value)
    }

    const handleSubmit = (e) =>{
        e.preventDefault()
        const details = {user_id:selectedUser,vehicle_id:selectedVehicle}
        const data ={details,navigate,token}
        dispatch(createDriverRequest(data))
    }
    useEffect(()=>{
        dispatch(fetchUsersRequest())
    },[dispatch])
    useEffect(()=>{
        dispatch(fetchVehicleRequest())
    },[dispatch])
if(error)return <p>{error}</p>
    return(
        <div>
            <h2>Create driver</h2>
            <form onSubmit={handleSubmit}>
            <label>Select a User:</label>
            <select value={selectedUser} onChange={handleUserChange}>
                <option value="">-- Select a User --</option>
                {users.map((item) => (
                    <option key={item._id} value={item._id}>
                        {item.name}
                    </option>
                ))}
            </select>
            <label>Select a Vehicle:</label>
            <select value={selectedVehicle} onChange={handleVehicleChange}>
                <option value={selectedUser}>-- Select a Vehicle --</option>
                {vehicles.map((item) => (
                    <option key={item._id} value={item._id}>
                        {item.type}
                    </option>
                ))}
            </select>
                {/* <input
                name="capacity"
                placeholder="Capacity"
                type="text"
                value={details.capacity}
                onChange={handleChange}
                />
                <input
                name="license_plate"
                placeholder="License plate"
                type="text"
                value={details.license_plate}
                onChange={handleChange}
                />               */}
                <button type="submit">Submit</button>
            </form>
        </div>
    )
}
export default CreateDrivers
