import {createSlice} from '@reduxjs/toolkit';

const initialState = {
    order: [],
    loading: false,
    error:null,
};

const orderSlice = createSlice({
    name:'order',
    initialState,
    reducers:{
        fetchOrderRequest:(state)=>{
            state.loading = true
        },
        fetchOrderSuccess:(state,action)=>{
            state.order= action.payload;
            state.loading=false
        },
        fetchOrderFailure:(state,action)=>{
            state.error = action.payload
        },
        fetchDriverOrderRequest:(state)=>{
            state.loading = true
        },
        fetchDriverOrderSuccess:(state,action)=>{
            state.order= action.payload;
            state.loading=false
        },
        fetchOrderFailure:(state,action)=>{
            state.error = action.payload
        },
        createOrderRequest:(state)=>{
            state.loading=true
        },
        createOrderSuccess:(state,action)=>{
            state.order.push(action.payload)
            state.loading=false
        },
        createOrderFailure:(state,action)=>{
            state.error=action.payload
            state.loading=false
        }
    }
})

export const {fetchOrderRequest,fetchOrderSuccess,fetchOrderFailure,createOrderRequest,createOrderSuccess,createOrderFailure} = orderSlice.actions

export default orderSlice.reducer
